import React, { useReducer, useState } from 'react';
import Reducer from './reducer';
import ActionType, { IUser } from './type';



export default function Login() {

    const [state, dispatch] = useReducer(Reducer, { loginedUser: null });
    const [formData, setState] = useState(IUser | null)(null);

    const onLogin = () => {

        if (formData.username && formData.password) {
            dispatch({
                type: "SUBMIT_LOGIN",
                payload: formData
            })
        }
        alert("Login Failed")
    }

    return <div>
        {
            state.loginedUser ? <button>Log Off</button> : <div>
                <input onChange={(e) => setState({ ...formData, username: e.target.value })} placeholder="user name" />
                <input onChange={(e) => setState({ ...formData, password: e.target.value })} placeholder="password" />
                <button>Login</button>
            </div>
        }
    </div>
}
