import ActionTypes, { State, IUser } from './type';

function checkLogin(state: State, payload: IUser) {

    console.log(state)
    alert(JSON.parse(payload))
}

function reducer(state: State, action: ActionTypes): State {

    switch (action.type) {
        case 'SET_USERS':
            return {
                loginSuccess: false,
                users: action.users
            }
        case "ON_LOGIN":
            return checkLogin(state, payload)
        default:
            return state
    }
}

export default reducer
