import ActionTypes from './type';

const initialState = {
    loginedUser: null,
    users: []
}

function checkLogin(state: any, payload) {

    console.log(state)
    alert(JSON.parse(payload))
}

function reducer(state = initialState, action: ActionTypes) {

    switch (action.type) {

        case "SUCCESS_LOGIN":
            return {
                loginedUser: action.payload
            }
        case 'SUBMIT_LOGIN':
            return checkLogin(state, action.payload)
        case 'SET_USERS':
            return action.payload
        default:
            return state
    }

}

export default reducer
