import React, { useReducer, useState } from 'react';
import Reducer from './reducer';
import ActionType, { IUser } from './type';

export default function Login() {

    const [state, dispatch] = useReducer(Reducer, { loginedUser: null });
    const [formData, setFormData] = useState<IUser | null>(null)

    const onLogin = () => {

        if (formData?.UserName && formData.Password) {
            dispatch({
                type: "SUBMIT_LOGIN",
                payload: formData
            })
        }
        alert("Login Failed")
    }

    return <div>
        {
            state.loginedUser ? <button>Log Off</button> : <div>
                <input onChange={(e) => setFormData({ UserName: e.target.value, Password: formData?.Password })} placeholder="user name" />
                <input onChange={(e) => setFormData({ Password: e.target.value, UserName: formData?.UserName })} placeholder="password" />
                <button onClick={onLogin}>Login</button>
            </div>
        }
    </div>
}
