import React, { useReducer, useState, useEffect } from 'react';
import Reducer from './reducer';
import { IUser, IState } from './type';

export default function Login() {

    const [state, dispatch] = useReducer(Reducer, { users: [] })
    const [formData, setFormData] = useState<IUser | null>(null);

    useEffect(() => {
        fetch('https://jsonplaceholder.typicode.com/users', {
            headers: {
                "Access-Control-Allow-Origin": "*",
                "Access-Control-Allow-Methods": "*",
            }
        }).then(response => response.json())
            .then(users => {
               dispatch({
                   type: 'SET_USERS',
                   users: users
               })
            })
    }, [])

    const onLogin = () => {
        if (formData?.UserName && formData.Password) {
            dispatch({
                type: "ON_LOGIN",
                user: formData
            })
        }
        alert("Login Failed")
    }

    return <div>
        {
            state.loginSuccess ? <button>Log Off</button> : <div>
                <input onChange={(e) => setFormData({ UserName: e.target.value, Password: formData?.Password })} placeholder="user name" />
                <input onChange={(e) => setFormData({ Password: e.target.value, UserName: formData?.UserName })} placeholder="password" />
                <button onClick={onLogin}>Login</button>
            </div>
        }
    </div>
}
