import ActionTypes, { State, IUser } from './type';

function reducer(state: State, action: ActionTypes): State {

    switch (action.type) {
        case 'SET_USERS':
            return {
                loginSuccess: false,
                users: action.users
            }
        case "ON_LOGIN_SUCCESS":
                return {
                    loginSuccess: true,
                    users: state.users
                }
        default:
            return state
    }
}

export default reducer
