import ActionTypes, { State } from './type';

// function checkLogin(state: any, payload: any) {

//     console.log(state)
//     alert(JSON.parse(payload))
// }

function reducer(state: State, action: ActionTypes): State {
    switch (action.type) {
        case 'SET_USERS':
            return {
                loginSuccess: false,
                users: action.users
            }
        default:
            return state
    }
}

export default reducer
